// api/products/api.ts
import { createAuthenticatedFetch } from '~/api/request'  // 用 ~ 也可以
// 或
import { createAuthenticatedFetch } from '@/api/request'  // 用 @ 也可以

const { public: { API_BASE_URL } } = useRuntimeConfig()

export async function fetchProductList() {
  const request = createAuthenticatedFetch()

